import React, { useState, useRef, useEffect } from "react";
import { Link } from "react-router-dom";
import {
    MicFill, 
    MicMuteFill,
    ArrowRepeat,
    PencilFill} from 'react-bootstrap-icons';
import SpeechRecognition, { useSpeechRecognition } from "react-speech-recognition";
import './PracticeCard.css';

function PracticeCard(props) {

    const [phase, setPhase] = useState('before') //options are before, during, after
    const [inputType, setInputType] = useState(null); //options are mic or keyboard
    const [speechSupported, setSpeechSupported] = useState(true);
    const [textInput, setTextInput] = useState('');
    const [editVoice, setEditVoice] = useState(false);
    const [answer, setAnswer] = useState('');
    const microphoneRef = useRef(null);
    const { transcript, resetTranscript } = useSpeechRecognition();

    const transcriptRef = useRef(transcript);

    if(!SpeechRecognition.browserSupportsSpeechRecognition()){
        setSpeechSupported(false)
    }

    const handleListening = () => {
        SpeechRecognition.startListening({
            continuous: true,
        })
    };

    const stopHandle = () => {
        SpeechRecognition.stopListening();
    }

    const handleReset = () => {
        stopHandle();
        resetTranscript();
        setTextInput('')
    }

    const handleTextChange = () => {

    }

    const handleFinish = () => {
        //trigger PUT request
        console.log(answer)
    }

    useEffect(() => {
        if(textInput){
            setPhase('after')
            setAnswer(textInput)
        }else{
            setPhase('before')
            setAnswer(textInput)
        }

    }, [textInput])

    //Review this block for use for editing voice dictation -------------- //
    useEffect(() => {

            console.log(transcriptRef.current)
            transcriptRef.current = transcript
            setAnswer(transcript)

    }, [transcript])

    // -------------------------------------------------------------------- //


  return (
    <div>
        {phase !== 'review' &&
        <div className="practice-container">
       {!transcript &&
        <div className="input-container">
            <div className="text-container">
                <br/>
                <textarea 
                    className="text-input" 
                    type='text' 
                    placeholder={`Type or click the mic to dictate`}
                    onChange={target => setTextInput(target.target.value)}
                >
                </textarea>
            </div>
        </div>}
        {
            transcript &&
            !editVoice &&
            <div className="input-container">
                <div className="text-input"> 
                    <p>{transcript}</p> 
                </div>
            </div>
        }
        {
            transcript &&
            editVoice &&
            <div className="input-container">
                <div className="text-input"> 
                    <textarea 
                        ref={transcriptRef}
                        onChange={handleTextChange}
                        className="text-input" 
                    ></textarea> 
                </div>
            </div>
        }
        {
            phase === 'before' &&
            speechSupported &&

            <div className="btn-container">
                <button 
                    className="circle-button mic-btn"
                    ref={microphoneRef}
                    onClick={() => {
                        setPhase('during');
                        setInputType('mic')
                        handleListening()
                    }}
                >
                    <MicFill className='mic-btn-img' />
                </button>
            </div>
        }
        {
            phase === 'during' &&
            inputType === 'mic' &&

            <div className="btn-container">
                <button 
                    className="mute circle-button mic-btn"
                    onClick={() => {
                        if(transcript){
                            setPhase('after')
                            stopHandle()
                        }else{
                            setPhase('before')
                            stopHandle()
                        }
                    }}
                >
                    <MicMuteFill className="mic-btn-img" />
                </button>
            </div>
        }
        {
            phase === 'after' &&

            <div className="hrz-btn-container">
                <button 
                    className="sm-cir-button tertiary-btn"
                    onClick={() => {
                        setPhase('before')
                        handleReset()
                    }}
                >
                    <ArrowRepeat className="restart" />
                </button>
                <button 
                    className="sm-cir-button tertiary-btn"
                    onClick={() => {
                        setEditVoice(true)
                    }}
                >
                    <PencilFill className="edit" />
                </button>
                <button 
                    className="sm-cir-button tertiary-btn"
                    onClick={() => {
                        setPhase('review')
                        handleFinish()
                    }}
                >

                        <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="finish bi bi-box-arrow-right" viewBox="0 0 16 16">
                            <path fillRule="evenodd" d="M10 12.5a.5.5 0 0 1-.5.5h-8a.5.5 0 0 1-.5-.5v-9a.5.5 0 0 1 .5-.5h8a.5.5 0 0 1 .5.5v2a.5.5 0 0 0 1 0v-2A1.5 1.5 0 0 0 9.5 2h-8A1.5 1.5 0 0 0 0 3.5v9A1.5 1.5 0 0 0 1.5 14h8a1.5 1.5 0 0 0 1.5-1.5v-2a.5.5 0 0 0-1 0v2z"/>
                            <path fillRule="evenodd" d="M15.854 8.354a.5.5 0 0 0 0-.708l-3-3a.5.5 0 0 0-.708.708L14.293 7.5H5.5a.5.5 0 0 0 0 1h8.793l-2.147 2.146a.5.5 0 0 0 .708.708l3-3z"/>
                        </svg>
                </button>
            </div>
        }
    </div>}
    {phase === 'review' &&
        <div className='practice-container review-card'>
            <p>Reviewing....</p>
        </div>
    }
    </div>
  );
}

export default PracticeCard;
