import React, { useState, useEffect } from "react";
import PracticeCard from "./PracticeCard";
import ToggleBar from "./ToggleBar";
import './Practice.css';
import { getLegend } from "../utils/utils";

function Practice() {

    const [cards, setCards] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [cardIndex, setCardIndex] = useState(0);

    useEffect(() => {
        setIsLoading(true)
        getCards();
        console.log(cards)
    },[])

    useEffect(() => {
        console.log(cards)
    }, [cards])

    const getCards = async () => {

        const setCardArry = (data) => {
            const cardData = data;
            setCards(cardData);
        }

        const cardArry = await getLegend();
        setCardArry(cardArry);
        setIsLoading(false);
    }

  return (
    <div>
        <ToggleBar queue={cards[cardIndex].que} />
        { isLoading && <p className='text-input'>Loading...</p> }
        {cards && cards.map((card, index) => {
                return <PracticeCard key={index} queue={card.queue}/>
            })
        }

    </div>
  );
}

export default Practice;
