import React, { useState, useRef, useEffect } from "react";
import SpeechRecognition, { useSpeechRecognition } from "react-speech-recognition";
import './Practice.css';

function Practice() {

    const [phase, setPhase] = useState('before') //options are before, during, after
    const [speechSupported, setSpeechSupported] = useState(true);
    const [isListening, setIsListening] = useState(false);
    const microphoneRef = useRef(null);
    const { transcript, resetTranscript } = useSpeechRecognition();

    if(!SpeechRecognition.browserSupportsSpeechRecognition()){
        setSpeechSupported(false)
    }

    const handleListening = () => {
        setIsListening(true);
        //microphoneRef.current.classList.add("listening");
        SpeechRecognition.startListening({
            continuous: true,
        })
    };

    const stopHandle = () => {
        setIsListening(false);
        //microphoneRef.current.classList.remove('listening');
        SpeechRecognition.stopListening();
    }

    const handleReset = () => {
        stopHandle();
        resetTranscript()
    }

  return (
    <div className="practice-container">
       {!transcript &&
        <div className="input-container">
            <div className="text-container">
                <textarea 
                    className="text-input" 
                    type='text' 
                    placeholder="Type or click the mic to dictate"
                >
                </textarea>
            </div>
        </div>}
        {
            transcript &&
            <div className="input-container">
                <div className="text-input"> 
                    <p>{transcript}</p> 
                </div>
            </div>
        }
        {
            phase === 'before' &&
            speechSupported &&

            <div className="btn-container">
                <button 
                    className="circle-button mic-btn"
                    ref={microphoneRef}
                    onClick={() => {
                        setPhase('during');
                        handleListening()
                    }}
                >
                    <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="mic-btn-img bi bi-mic-fill" viewBox="0 0 16 16">
                        <path d="M5 3a3 3 0 0 1 6 0v5a3 3 0 0 1-6 0V3z"/>
                        <path d="M3.5 6.5A.5.5 0 0 1 4 7v1a4 4 0 0 0 8 0V7a.5.5 0 0 1 1 0v1a5 5 0 0 1-4.5 4.975V15h3a.5.5 0 0 1 0 1h-7a.5.5 0 0 1 0-1h3v-2.025A5 5 0 0 1 3 8V7a.5.5 0 0 1 .5-.5z"/>
                    </svg>
                </button>
            </div>
        }
        {
            phase === 'during' &&

            <div className="btn-container">
                <button 
                    className="mute circle-button mic-btn"
                    onClick={() => {
                        setPhase('after')
                        stopHandle()
                    }}
                >
                    <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="mic-btn-img bi bi-mic-mute-fill" viewBox="0 0 16 16">
                        <path d="M13 8c0 .564-.094 1.107-.266 1.613l-.814-.814A4.02 4.02 0 0 0 12 8V7a.5.5 0 0 1 1 0v1zm-5 4c.818 0 1.578-.245 2.212-.667l.718.719a4.973 4.973 0 0 1-2.43.923V15h3a.5.5 0 0 1 0 1h-7a.5.5 0 0 1 0-1h3v-2.025A5 5 0 0 1 3 8V7a.5.5 0 0 1 1 0v1a4 4 0 0 0 4 4zm3-9v4.879L5.158 2.037A3.001 3.001 0 0 1 11 3z"/>
                        <path d="M9.486 10.607 5 6.12V8a3 3 0 0 0 4.486 2.607zm-7.84-9.253 12 12 .708-.708-12-12-.708.708z"/>
                    </svg>
                </button>
            </div>
        }
        {
            phase === 'after' &&

            <div className="vrt-btn-container">
                <button 
                    className="primary-button"
                    onClick={() => {
                        setPhase('after')
                    }}
                >
                    Finish
                </button>
                <button 
                    className="primary-button"
                    onClick={() => {
                        setPhase('before')
                        handleReset()
                    }}
                >
                    Edit
                </button>
                <button 
                    className="primary-button"
                    onClick={() => {
                        setPhase('before')
                        handleReset()
                    }}
                >
                    Restart
                </button>
            </div>
        }
    </div>
  );
}

export default Practice;
