import React, { useState, useEffect } from "react";
import PracticeCard from "./PracticeCard";
import './Practice.css';
import { getLegend } from "../utils/utils";

function Practice() {

    const [cards, setCards] = useState([]);
    const [isLoading, setIsLoading] = useState(false)

    useEffect(() => {
        setIsLoading(true)
        getCards();
        console.log(cards)

    }, [])

    const getCards = async () => {

        const setCardArry = (crdArry) => {
            console.log(crdArry)
            setCards(crdArry);
        }

        const cardArry = await getLegend()
        setCardArry(cardArry);
        setIsLoading(false);
    }

  return (
    <div>
        <p>Hello</p>
        {
            isLoading &&
            <p className='text-input'>Loading...</p>
        }
        <div>
            {
            cards.map(card =>
                <p>{card.queue}</p>
            )
            }
        </div>
        {
            cards &&
            cards.map(card => {
                <div className="practice-container">
                    <PracticeCard queue={card.queue} />
                </div>
            })
        }
    </div>
  );
}

export default Practice;
