import { v4 as uuid4 } from 'uuid';

export const API_ENDPOINT = process.env.PORT ? 'production URL' : 'http://localhost:3000';

export const getCategories = async (catId) => {
    const response = await fetch(`${API_ENDPOINT}/categories`,{
        method: "GET",
        headers: {
            "Content-Type": "application/json",
        },
        body: {
            "catId": catId
        }
    })

    const categories = await response.json();

    return categories
}
