import React, { useEffect, useState } from "react";
import { Link } from 'react-router-dom';
import { getAnswer, getLegend, scoreAnswer } from '../utils/utils';
import './Home.css';

function Review() {

    const [accuracy, setAccuracy] = useState('90%');
    const [answer, setAnswer] = useState(null);
    const [legend, setLegend] = useState(null);
    const [isLoading, setIsLoading] = useState(true);

    // use effect 
    // async get request for the user answer and legend
    // once data, run review algorithm
    // map completed answer
    useEffect(() => {

        setIsLoading(true);
        getResults();

    }, [])

    useEffect(() => {
        console.log(legend)

    }, [answer, legend]) 

    const getResults = async () => {

        const setResults = (ans, leg) => {
            console.log(leg)
            console.log(ans)
            const [answer] = scoreAnswer(ans, leg)
            setLegend(leg);
            setAnswer(ans);
        }
        //replace with api request
        const leg =  getLegend();
        const ans =  getAnswer();

        setResults(ans, leg);
        setIsLoading(false);
        console.log(legend);
    }

  return (
    <div>
        {   answer &&
            legend &&
            !isLoading &&
        <div className="review-container">
            <div className="accuracy-val">
                <p>{accuracy}</p>
            </div>
            <div className="review-text-container">
                <div className="review-text"> 
                    {legend !== null && legend.map( word => {
                        return(
                            <p clsasName={word.correct}>{word.word}</p>
                        )
                    })}
                </div>
            </div>
        </div>
        }
        {
            !answer &&
            !legend &&

            <div>
                <p>Loading...</p>
            </div>
        }
    </div>

  );
}

export default Review;
