import React, { useState, useEffect } from "react";
import './LegendInput.css';
import DropDown from "./DropDown";
import { PlusLg } from 'react-bootstrap-icons';
import { getCategories, addLegend } from "../utils/api";

function LegendInput (props) {

    const [queue, setQueue] = useState();
    const [category, setCategory] = useState('Select from drop down');
    const [categories, setCategories] = useState();
    const [catId, setCatId] = useState();
    const [isLoading, setIsLoading] = useState();
    const [legend, setLegend] = useState(null);
    const [newCat, setNewCat] = useState(false);
    const [editData, setEditData] = useState(props.initialData)

    const setCategoryOptions = async () => {

        const setData = (catData) => {
            setCategories(catData);
        }
        const data = await getCategories();
        setData(data);
        setIsLoading(false)

    }

    const handleSubmit = (e) => {
        console.log(category, catId, queue, legend)
        if(!category || !catId || !queue || !legend){
            e.preventDefault();
            alert('Please fill out all fields')
            return;
        }else if(editData){
            //updateLegend()
        }else{
            addLegend(catId, queue, legend)
        }
    }

    useEffect(() => {
        setIsLoading(true)
        setCategoryOptions()
        if(editData){
            setQueue(editData.queue)
            setLegend(editData.legend)
            setCategory(editData.category)
            setCatId(editData.catId)
        }
    }, [])

    useEffect(() => {   
        if(categories){
            for(let i=0; i < categories.length; i++){
                if(categories[i].category === category){
                    setCatId(categories[i].id)
                }
            }
        }
    }, [category])

    if(isLoading){
        return(
            <div>
                <p>Loading...</p>
            </div>
        )
    }else{
        return(
            <div className="form-container">
                <form>
                {!editData &&
                    <div>
                        <label className="def-text label">Library</label>
                        <br/>
                        <DropDown 
                            options={categories}
                            choice={category}
                            setChoice={setCategory}
                        />
                        <br/>
                    </div>
                    }
                    {
                        newCat &&
                        <div>
                            <input 
                                id='category'
                                type='text'
                                placeholder="New category"
                                required
                                onChange={(e) => setCategory(e.currentTarget.value)}
                            />
                        </div>
                    }
                    <label className="def-text label">Queue</label>
                    <br/>
                    <input
                        id='queue'
                        type='text'
                        required
                        value={editData ? editData.queue : null}
                        onChange={(e) => setQueue(e.currentTarget.value)}
                    />
                    <br/>
                    <label className="def-text label">Key</label>
                    <br/>
                    <textarea
                        id='legend'
                        type='text'
                        required
                        value={editData ? editData.legend : null}
                        onChange={(e) => setLegend(e.currentTarget.value)}
                    />
                    {editData &&

                        <button 
                            className="primary-button" 

                        >
                            Cancel
                        </button>

                    }
                    <button
                        type="submit"
                        className="primary-button"
                        onClick={e => handleSubmit(e)}
                    >Submit</button>
                </form>
            </div>
        )
    }

}

export default LegendInput;
